/**
 * @author hanoli
 *
 */
@Service
public class IExportPdfImpl implements IExportPdf{

	// Document document = new Document();
	 private static Document document;
	 
	public ByteArrayInputStream pdfReport(Folio folio,List<Imagen> imagen) {

		ByteArrayOutputStream out = new ByteArrayOutputStream(); 
		 document = new Document(PageSize.A4, 70, 50, 50, 50);
		  
		  try {
			  PdfWriter.getInstance(document, out);
			  document.open();
			  document .add( new Chunk( "" ));
			  agregarEncabezado();
			  agregarEncabezadoFolio(folio);
			  agregarDatosCliente(folio);
			  agregarDetalle(folio);
			  
			  for (int i = 0; i < imagen.size(); i++) {
				  agregarFotos(imagen.get(i));
			  }
			
	

      } catch (DocumentException e) {
          e.printStackTrace();
      }finally{
			document.close();
		}

		    
	        return new ByteArrayInputStream(out.toByteArray());
	}

	
	private void agregarEncabezado(){
		
			PdfPTable tabla = new PdfPTable(6);

			PdfPCell cellLogo = new PdfPCell(obtieneLogo(), false);
		
			cellLogo.setHorizontalAlignment(Element.ALIGN_RIGHT);
			cellLogo.setBorder(0);
			tabla.addCell(cellLogo);
			tabla.addCell(celdaEncabezado("FORMATO DE RECEPCION DE EQUIPO"));
			tabla.addCell(celdaEncabezado(""));
			tabla.addCell(celdaEncabezado(""));
			tabla.addCell(celdaEncabezado(""));
			tabla.addCell(celdaEncabezado(""));
			
			
			
		try {
			agregarSalto();
			document.add(tabla);
		} catch (DocumentException e) {

		}
	}
	
	
	private void agregarFotos(Imagen imagen){
		
		PdfPTable tabla = new PdfPTable(1);

		PdfPCell cellImg = new PdfPCell(obtieneBase64(imagen.getImagen()), false);
	
		cellImg.setHorizontalAlignment(Element.ALIGN_CENTER);
		cellImg.setBorder(0);
		
		tabla.addCell(cellImg);
				
		
	try {
		agregarSalto();
		document.add(tabla);
	} catch (DocumentException e) {

	}
}
	
	
	private void agregarEncabezadoFolio(Folio folio){
	    	
			PdfPTable tabla = new PdfPTable(4);
		
			SimpleDateFormat dt = new SimpleDateFormat("dd/MM/yyyy"); 
			
			tabla.addCell(celdaFolioFecha("FOLIO:"));
			tabla.addCell(celdaDatosFolioFecha(folio.getFolio()));
			tabla.addCell(celdaFolioFecha("FECHA:"));
			tabla.addCell(celdaDatosFolioFecha(dt.format(folio.getFecha())));
		
		try {
			document.add(tabla);
		} catch (DocumentException e) {
		}
	}
	
	private void agregarDatosCliente(Folio folio){
		agregarSalto();
	
		PdfPTable tabla = new PdfPTable(2);
		
		tabla.addCell(celdaFolioFecha("Cliente:"));
		tabla.addCell(celdaDatosFolioFecha(folio.getCliente().getNombre()+"."+folio.getCliente().getApellidoPat()+"."+folio.getCliente().getApellidoMat()));
		
		
		
	
	try {
		document.add(tabla);
	} catch (DocumentException e) {
	}
}
	
	
	private void agregarDetalle(Folio folio){
		    agregarSalto();
			PdfPTable tabla = new PdfPTable(5);
			try {	

			
				tabla.addCell(celdaEncabezadoColum("TIPO DE EQUIPO"));
				tabla.addCell(celdaEncabezadoColum("MARCA"));
				tabla.addCell(celdaEncabezadoColum("MODELO"));
				tabla.addCell(celdaEncabezadoColum("NUM.SERIE"));
				tabla.addCell(celdaEncabezadoColum("COMENTARIOS"));
			
				tabla.addCell(celdaDatos(folio.getTipoEquipo()));
				tabla.addCell(celdaDatos(folio.getMarca()));
				tabla.addCell(celdaDatos(folio.getModelo()));
				tabla.addCell(celdaDatos(folio.getNumSerie()));
				tabla.addCell(celdaDatos(folio.getComentarios()));
		
				document.add(tabla);
		
			} catch (DocumentException e ) {
				
			} catch (Exception e) {
				
			}
	}
	
	public Image obtieneLogo(){
		Image logo = null;
		try
		{
			logo = Image.getInstance("C:\\logo.png");
			logo.scaleToFit(125,50);
			logo.setAlignment(Chunk.ALIGN_MIDDLE);
			logo.setBorderWidth(5);
		}
		catch ( Exception e ){
			
		}
		
		return logo;
	}
	
	
	public Image obtieneBase64(String base64){
		
		  //String base64 = "";
		    byte[] decoded = org.apache.commons.codec.binary.Base64.decodeBase64(base64.getBytes());
		    Image foto = null;
			try {
				try {
					foto = Image.getInstance(decoded);
					foto.scaleToFit(250,180);
				} catch (BadElementException e) {
					// TODO Auto-generated catch block
					e.printStackTrace();
				}
				
			} catch (MalformedURLException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			} catch (IOException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		
		return foto;
	}
	
	
	private PdfPCell celdaFolioFecha(String strColumna){
		
		PdfPCell celdaEncabezado ;

		Paragraph paragraph = new Paragraph(strColumna,FontFactory.getFont("ARIAL", 10,Font.BOLD,BaseColor.BLACK));
		celdaEncabezado = new PdfPCell(paragraph);
		//celdaEncabezado.setBackgroundColor(BaseColor.ORANGE);
		celdaEncabezado.setBorder(0);
		celdaEncabezado.setVerticalAlignment(Element.ALIGN_MIDDLE);
		celdaEncabezado.setHorizontalAlignment(Element.ALIGN_RIGHT);
		
		return celdaEncabezado;
	}
	
	private PdfPCell celdaDatosFolioFecha(String strColumna){
		
		PdfPCell celdaEncabezado ;

		Paragraph paragraph = new Paragraph(strColumna,FontFactory.getFont("ARIAL", 10,BaseColor.BLACK));
		celdaEncabezado = new PdfPCell(paragraph);
		celdaEncabezado.setHorizontalAlignment(Element.ALIGN_LEFT);
		//celdaEncabezado.setBackgroundColor(BaseColor.ORANGE);
		celdaEncabezado.setBorder(0);
		celdaEncabezado.setVerticalAlignment(Element.ALIGN_MIDDLE);
		celdaEncabezado.setHorizontalAlignment(Element.ALIGN_LEFT);
		
		return celdaEncabezado;
	}
	
	
	private PdfPCell celdaEncabezadoColum (String strColumna){
		
		PdfPCell celdaEncabezado ;

		Paragraph paragraph = new Paragraph(strColumna,FontFactory.getFont("ARIAL", 8,Font.BOLD,BaseColor.BLACK));
		celdaEncabezado = new PdfPCell(paragraph);
		celdaEncabezado.setBackgroundColor(BaseColor.LIGHT_GRAY);
		//celdaEncabezado.setBorder(0);
		celdaEncabezado.setVerticalAlignment(Element.ALIGN_MIDDLE);
		celdaEncabezado.setHorizontalAlignment(Element.ALIGN_CENTER);
		
		return celdaEncabezado;
	}
	
	
	private PdfPCell celdaEncabezado (String strColumna){
		
		PdfPCell celdaEncabezado ;

		Paragraph paragraph = new Paragraph(strColumna,FontFactory.getFont("ARIAL", 15,Font.BOLD,BaseColor.BLACK));
		celdaEncabezado = new PdfPCell(paragraph);
		celdaEncabezado.setColspan(5);
		celdaEncabezado.setPadding(5);
		celdaEncabezado.setBorder(0);
		celdaEncabezado.setHorizontalAlignment(Element.ALIGN_LEFT);
		
		return celdaEncabezado;
	}
	
	
	private PdfPCell celdaDatos (String strColumna){
		
		PdfPCell celdaDatos ;
		
		//Chunk chunk = new Chunk(strColumna);
        //Paragraph paragraph = new Paragraph(chunk);

		Paragraph paragraph = new Paragraph(strColumna,FontFactory.getFont("ARIAL", 10,Font.NORMAL,BaseColor.BLACK));
		//paragraph.setFont(FontFactory.getFont("ARIAL", 5,Font.BOLD,BaseColor.BLACK));
		celdaDatos = new PdfPCell(paragraph);
		celdaDatos.setBorder(0);
		celdaDatos.setVerticalAlignment(Element.ALIGN_MIDDLE);
		celdaDatos.setHorizontalAlignment(Element.ALIGN_CENTER);
		
		
		return celdaDatos;
	}
	

	private void agregarSalto(){
	try {
		document.add(new Paragraph(" "));
	} catch (DocumentException e) {
		
	}
	}
	
	
}
